css:
.highlight.ide-haskell-lint .region {
  background: linear-gradient(to right, #0098ff 0%, rgba(0, 152, 255, 0.5) 100%);
}
.highlight.ide-haskell-warning .region {
  background: linear-gradient(to right, #ffaa2c 0%, rgba(255, 170, 44, 0.5) 100%);
}
.highlight.ide-haskell-error .region {
  background: linear-gradient(to right, #cc0000 0%, rgba(204, 0, 0, 0.5) 100%);
}
.highlight.ide-haskell-type .region {
  background: rgba(0, 152, 255, 0.1);
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 0.8em;
  width: 0.8em;
  height: 0.8em;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-lint {
  visibility: visible;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-lint:before {
  content: "\f059";
  color: #5293d8;
  z-index: 1;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-warning {
  visibility: visible;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-warning:before {
  content: "\f08f";
  color: #ff982d;
  z-index: 2;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-error {
  visibility: visible;
}
.gutter[gutter-name="ide-haskell-check-results"] .decoration.ide-haskell-error:before {
  content: "\f02d";
  color: #cc0000;
  z-index: 3;
}
.scroll-view > .lines {
  pointer-events: none;
}
