css:
@-webkit-keyframes ide-haskell-move {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 50px 50px;
  }
}
ide-haskell-panel {
  display: flex;
  flex-direction: column;
}
ide-haskell-panel resize-handle {
  position: absolute;
}
ide-haskell-panel[data-pos=top],
ide-haskell-panel[data-pos=bottom] {
  min-height: 120px;
  max-height: 50vh;
  width: auto !important;
}
ide-haskell-panel[data-pos=top] resize-handle,
ide-haskell-panel[data-pos=bottom] resize-handle {
  cursor: row-resize;
  height: 10px;
  left: 0;
  right: 0;
}
ide-haskell-panel[data-pos=left],
ide-haskell-panel[data-pos=right] {
  min-width: 120px;
  max-width: 50vw;
  height: auto !important;
}
ide-haskell-panel[data-pos=left] resize-handle,
ide-haskell-panel[data-pos=right] resize-handle {
  cursor: col-resize;
  width: 10px;
  top: 0;
  bottom: 0;
}
ide-haskell-panel[data-pos=top] resize-handle {
  bottom: 0;
}
ide-haskell-panel[data-pos=bottom] resize-handle {
  top: 0;
}
ide-haskell-panel[data-pos=left] resize-handle {
  right: 0;
}
ide-haskell-panel[data-pos=right] resize-handle {
  left: 0;
}
ide-haskell-panel ide-haskell-panel-heading {
  display: flex;
  flex-direction: row;
  flex-shrink: 0;
  flex-wrap: wrap;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon {
  padding-top: 3px;
  margin: 0px 0.5em;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  display: block;
  text-align: center;
  font-size: 1.3em;
  content: "\f03a";
  width: 15pt;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon[data-status=progress]:before {
  content: "\f09e";
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon[data-status=ready]:before {
  content: "\f03a";
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon[data-status=error]:before {
  color: #ff6347;
  content: "\f02d";
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-status-icon[data-status=warning]:before {
  content: "\f08f";
  color: #e2c08d;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-button {
  flex-shrink: 0;
  flex-grow: 0;
  display: inline-block;
  line-height: 1.8em;
  color: rgba(157, 165, 180, 0.6);
  border: 1px solid #181a1f;
  background: rgba(53, 59, 69, 0.33);
  cursor: pointer;
  vertical-align: middle;
  height: 1.7em;
  white-space: nowrap;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-button:active {
  background: transparent;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-button.cancel {
  width: 2em;
  border-radius: 3px;
  margin-left: 5px;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-button.cancel:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  display: block;
  text-align: center;
  font-size: 1.7em;
  content: "\f081";
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button {
  text-transform: capitalize;
  margin-right: -1px;
  padding: 0 .6em;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button:first-of-type {
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button:last-of-type {
  margin-right: .6em;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button.active {
  color: #d7dae0;
  background: #353b45;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button:before {
  content: attr(data-caption);
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-buttons-container ide-haskell-button:not([data-count="0"]):before {
  content: attr(data-caption) ' (' attr(data-count) ')';
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-checkbox {
  flex-shrink: 0;
  flex-grow: 0;
  display: inline-block;
  line-height: 1.8em;
  color: rgba(157, 165, 180, 0.6);
  border: 1px solid #181a1f;
  background: rgba(53, 59, 69, 0.33);
  cursor: pointer;
  vertical-align: middle;
  border-radius: 3px;
  width: 2em;
  height: 1.8em;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-checkbox:active {
  background: transparent;
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-checkbox:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  display: block;
  text-align: center;
  font-size: 1.7em;
  content: "\f016";
}
ide-haskell-panel ide-haskell-panel-heading ide-haskell-panel-buttons ide-haskell-checkbox.enabled:before {
  content: "\f011";
}
ide-haskell-panel ide-haskell-param {
  font-size: 1.2em;
  margin: 0 0.5em;
}
ide-haskell-panel ide-haskell-progress-bar {
  display: block;
  flex-grow: 1;
  min-width: 50px;
  max-width: 300px;
  height: 1.25em;
  margin-top: 0.25em;
  border: 1px solid;
  border-radius: 3px;
  position: relative;
  margin-left: 1em;
  visibility: hidden;
}
ide-haskell-panel ide-haskell-progress-bar.visible {
  visibility: visible;
}
ide-haskell-panel ide-haskell-progress-bar.visible > span {
  display: block;
  background-color: #0088ff;
  position: absolute;
  height: 100%;
  background-image: -webkit-linear-gradient(right, #006dcc 37%, #0088ff 69%);
  box-shadow: inset 0 2px 9px rgba(255, 255, 255, 0.3), inset 0 -2px 6px rgba(0, 0, 0, 0.4);
  transition: width 0.1s linear;
}
ide-haskell-panel ide-haskell-progress-bar.visible > span:after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  background-image: -webkit-linear-gradient(-45deg, rgba(255, 255, 255, 0.2) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.2) 75%, transparent 75%, transparent);
  z-index: 1;
  background-size: 50px 50px;
  -webkit-animation: ide-haskell-move 2s linear infinite;
}
ide-haskell-panel ide-haskell-panel-items {
  display: block;
  overflow-y: auto;
  padding: 3px 10px;
  height: 100%;
  flex-grow: 1;
}
ide-haskell-panel ide-haskell-panel-items ide-haskell-panel-item {
  padding: 0.5em 0;
  margin: 0;
  white-space: normal;
}
ide-haskell-panel ide-haskell-panel-items ide-haskell-panel-item ide-haskell-item-position {
  cursor: pointer;
  display: block;
}
ide-haskell-panel ide-haskell-panel-items ide-haskell-panel-item ide-haskell-item-description {
  display: block;
  padding: 0.5em 1em;
  color: #c8ccd5;
  background-color: #21252b;
  font-family: Consolas, monospace;
  white-space: pre-wrap;
  border-radius: 0.5em;
}
ide-haskell-tooltip {
  display: block;
  background: #282c34;
  border: solid 2px #181a1f;
  padding: 5px;
  border-radius: 0 0.5em;
}
ide-haskell-tooltip::before {
  border-width: 0 0.5ch 0.5ch;
  border-style: solid;
  border-color: #181a1f transparent;
  display: block;
  width: 0;
  position: absolute;
  top: -0.5ch;
  left: 0;
  height: 0.5ch;
  background-size: 50% 100%;
  background-repeat: no-repeat;
  margin: 0;
  padding: 0;
  content: "";
}
ide-haskell-tooltip div {
  text-align: left;
  font-size: 85%;
  display: block;
  white-space: pre-wrap;
  background: none !important;
  word-break: break-all;
}
ide-haskell-tooltip div + div {
  margin-top: 1em;
}
.select-list.ide-haskell .select-list-heading {
  color: #d7dae0;
  font-size: 150%;
  text-align: center;
}
atom-overlay.ide-haskell {
  pointer-events: none;
}
.platform-win32 ide-haskell-panel resize-handle {
  cursor: ns-resize;
}
